model.journeyengine_recipes.ga_proc.raw_sql:
-- depends_on: {{ ref('ga_conversions') }}
{% set accounts = get_column_values(table=ref('accounts_proc'), column='bigquery_name', max_records=50, filter_column='platform', filter_value='Google Analytics') %}

{% if accounts != '' %}

with ga_report as (

	    {% for account in accounts %}

	    	{% set leads = get_column_values(table=ref('ga_conversions'), column='goal_name', max_records=50, filter_column='goal_type', filter_value='MQL', filter_column_2='bigquery_name', filter_value_2=account ) %}
	    	{% set sqls = get_column_values(table=ref('ga_conversions'), column='goal_name', max_records=50, filter_column='goal_type', filter_value='SQL', filter_column_2='bigquery_name', filter_value_2=account ) %}
	    	{% set excluded = get_column_values(table=ref('ga_conversions'), column='goal_name', max_records=50, filter_column='goal_type', filter_value='Exclude', filter_column_2='bigquery_name', filter_value_2=account ) %}
	    	{% set other = get_column_values(table=ref('ga_conversions'), column='goal_name', max_records=50, filter_column='goal_type', filter_value='Other', filter_column_2='bigquery_name', filter_value_2=account ) %}

		   	SELECT
		   	'{{account}}' as bigquery_name,
		   	'Google Analytics' as lookup_platform,
			lower(trim(regexp_replace(replace(replace(replace(replace(CONCAT(hostname,landingpagepath),'www.',''),'http://',''),'https://',''),'.html',''),r'\?.*$',''),'/')) as url,
			date,
			lower(source) source,
			lower(medium) medium,
			sessions,
			goalcompletionsall goal_completions,
			## goal completion columns
			{% if leads != [] %}
				{% for goal in leads %}
					cast(goal{{goal}}completions as int64) 
					{% if not loop.last %} + {% endif %} 
					{% if loop.last %} as mqls, {% endif %} 
				{% endfor %}
			{% else %}				
				null as mqls,		
			{% endif %}
			{% if sqls != [] %}
				{% for goal in sqls %}
					cast(goal{{goal}}completions as int64) 
					{% if not loop.last %} + {% endif %} 
					{% if loop.last %} as sqls, {% endif %} 
				{% endfor %}
			{% else %}				
				null as sqls,		
			{% endif %}			
			{% if excluded != [] %}
				{% for goal in excluded %}
					cast(goal{{goal}}completions as int64) 
					{% if not loop.last %} + {% endif %} 
					{% if loop.last %} as excluded, {% endif %} 
				{% endfor %}
			{% else %}				
				null as excluded,		
			{% endif %}		
			{% if other != [] %}
				{% for goal in other %}
					cast(goal{{goal}}completions as int64) 
					{% if not loop.last %} + {% endif %} 
					{% if loop.last %} as other, {% endif %} 
				{% endfor %}
			{% else %}				
				null as other,		
			{% endif %}			
			_sdc_sequence,
			first_value(_sdc_sequence) OVER (PARTITION BY hostname, landingpagepath, date, source, medium ORDER BY _sdc_sequence DESC) lv
			FROM `{{ target.project }}.ga_{{account}}.report` 

		    {% if not loop.last %} UNION ALL {% endif %}
	   {% endfor %}

)


SELECT 
bigquery_name,
lookup_platform,
date,
url,
source,
medium,
sum(sessions) sessions,
sum(goal_completions) goal_completions,
sum(mqls) mqls,
sum(sqls) sqls,
sum(excluded) excluded,
sum(other) other
FROM ga_report
where lv = _sdc_sequence
group by bigquery_name, lookup_platform, date, url, source, medium

{% endif %}

model.journeyengine_recipes.fb_ads.raw_sql:
-- depends_on: {{ ref('fb_adcreative') }}

{% set accounts = get_column_values(table=ref('accounts_proc'), column='bigquery_name', max_records=50, filter_column='platform', filter_value='Facebook Ads') %}

{% if accounts != [] %}

with fb_ads as (

	    {% for account in accounts %}
		   	SELECT 
			id ad_id,
			creative.id creative_id
			FROM  {{ most_recent_record(
                        relation='`' ~ target.project ~ '.fb_ads_' ~ account ~ '.ads`',
                        pk_fields=['id']
                  )}}
		     {% if not loop.last %} UNION ALL {% endif %}
	   {% endfor %}

)

SELECT
a.ad_id,
b.creative_id,
b.url
FROM (
    SELECT
    ad_id,
    creative_id,
    FROM fb_ads
    ) a
LEFT JOIN {{ref('fb_adcreative')}} b
ON (
	a.creative_id = b.creative_id 
)

{% endif %}

model.journeyengine_recipes.fb_adcreativ.raw_sql:
{% set accounts = get_column_values(table=ref('accounts_proc'), column='bigquery_name', max_records=50, filter_column='platform', filter_value='Facebook Ads') %}

{% if accounts != [] %}

with fb_adcreative as (


      {% for account in accounts %}
        SELECT 
        {{ fill('fb_ads', 'adcreative', account,
        ['id',
        'object_url',
      'url_tags',
      '_sdc_sequence']) }}
            FROM  {{ most_recent_record(
                        relation='`' ~ target.project ~ '.fb_ads_' ~ account ~ '.adcreative`',
                        pk_fields=['id']
                  )}}
        {% if not loop.last %} UNION ALL {% endif %}
     {% endfor %}
)

select creative_id, max(url) url
from (
  select
    id creative_id,
    lower(trim(regexp_replace(replace(replace(replace(replace(object_url,'www.',''),'http://',''),'https://',''),'.html',''),r'\?.*$',''),'/')) as url
  from fb_adcreative
  )
group by creative_id

{% endif %}

model.journeyengine_recipes.adwords_urls.raw_sql:
{% set accounts = get_column_values(table=ref('accounts_proc'), column='bigquery_name', max_records=50, filter_column='platform', filter_value='Google Ads') %}

{% if accounts != [] %}

with adwords_urls as (
      {% for account in accounts %}

	SELECT  
	campaignid,
	max(finalurl) finalurl
	FROM `{{ target.project }}.google_ads_{{account}}.FINAL_URL_REPORT`
	GROUP BY campaignid
		    {% if not loop.last %} UNION ALL {% endif %}
            {% endfor %}
)

SELECT
campaignid,
lower(trim(regexp_replace(replace(replace(replace(replace(finalurl,'www.',''),'http://',''),'https://',''),'.html',''),r'\?.*$',''),'/')) as url
FROM adwords_urls

 {% endif %}

